import Heap from './Heap.js';
import keeporder from './keeporder.js';
import nextchild from './nextchild.js';
import siftdown from './siftdown.js';
import siftup from './siftup.js';

export {Heap, keeporder, nextchild, siftdown, siftup};

import {increasing} from '@total-order/primitive';

const keeporder = (compare) => (a, b) =>
	compare(a[0], b[0]) || increasing(a[1], b[1]);

export default keeporder;

import assert from 'assert';

/**
 * Computes which child is the smallest according
 * to a comparison function.
 *
 * Hypothesis : i < j i.e. there should be at least one child
 *
 * @param {function} compare the comparison function
 * @param {array} a the array where the heap is stored
 * @param {number} i is the first child
 * @param {number} j - 1 is the last leaf
 *
 * @return {number}
 */
export default function nextchild(compare, a, i, j) {
	assert(i < j);
	if (j - i === 1) return i;

	assert(j - i >= 2);
	return compare(a[i], a[i + 1]) <= 0 ? i : i + 1;
}

import nextchild from './nextchild.js';

/**
 * Sifts down a node.
 *
 * @param {function} compare the comparison function
 * @param {array} a the array where the heap is stored
 * @param {number} i is the root element
 * @param {number} j - 1 is the last leaf
 * @param {number} k is the target node
 */

export default function siftdown(compare, a, i, j, k) {
	let current = k - i;

	while (true) {
		// Address of the first child in a zero-based
		// binary heap

		const firstchild = 2 * current + 1;

		// If current node has no children
		// then we are done

		if (firstchild >= j - i) break;

		// If current value is smaller than its smallest
		// child then we are done

		const candidate = nextchild(compare, a, i + firstchild, j);

		if (compare(a[i + current], a[candidate]) <= 0) break;

		// Otherwise
		// swap with smallest child

		const tmp = a[i + current];
		a[i + current] = a[candidate];
		a[candidate] = tmp;

		current = candidate - i;
	}

	return i + current;
}

/**
 * Sifts up a node.
 *
 * @param {function} compare the comparison function
 * @param {array} a the array where the heap is stored
 * @param {number} i is the root element
 * @param {number} j - 1 is the last leaf
 * @param {number} k is the target node
 */

export default function siftup(compare, a, i, j, k) {
	let current = k - i;

	// While we are not the root

	while (current !== 0) {
		// Address of the parent in a zero-based
		// d-ary heap

		const parent = i + ((current - 1) >>> 1);

		// If current value is greater than its parent
		// then we are done

		if (compare(a[i + current], a[parent]) >= 0) return i + current;

		// Otherwise
		// swap with parent

		const tmp = a[i + current];
		a[i + current] = a[parent];
		a[parent] = tmp;

		current = parent - i;
	}

	return i + current;
}

import {siftdown, Heap} from './core/index.js';

/**
 * Builds a heap in O(n) operations.
 *
 * @param {function} compare the comparison function
 * @param {array} x the array where the heap is stored
 *
 */

export default function heapify(compare, x) {
	const n = x.length;

	for (let k = (n / 2) | 0; k; ) {
		siftdown(compare, x, 0, n, --k);
	}

	return new Heap(compare, x);
}

import {IndexError} from '@failure-abstraction/error';
import {siftdown} from './core/index.js';

export default function heappop(heap) {
	const x = heap.data;
	const n = x.length;

	if (n === 0) throw new IndexError('heappop');

	const last = n - 1;

	// Swap last leaf and root

	const tmp = x[0];
	x[0] = x[last];
	x[last] = tmp;

	// Sift down the new root

	siftdown(heap.compare, x, 0, last, 0);

	// Return old root

	return x.pop();
}

import {siftup} from './core/index.js';

export default function heappush(heap, item) {
	const x = heap.data;
	const n = x.length;

	x.push(item);

	// Sift up the new leaf

	siftup(heap.compare, x, 0, n + 1, n);
}

import {siftdown} from './core/index.js';

export default function heappushpop(heap, item) {
	const x = heap.data;
	const n = x.length;

	if (n === 0) return item;

	const compare = heap.compare;

	if (compare(item, x[0]) <= 0) return item;

	const smallest = x[0];

	x[0] = item;

	// Sift down the new root

	siftdown(compare, x, 0, n, 0);

	return smallest;
}

import {IndexError} from '@failure-abstraction/error';
import {siftdown} from './core/index.js';

export default function heapreplace(heap, item) {
	const x = heap.data;
	const n = x.length;

	if (n === 0) throw new IndexError('heapreplace');

	const oldroot = x[0];

	x[0] = item;

	// Sift down the new root

	siftdown(heap.compare, x, 0, n, 0);

	return oldroot;
}

import * as core from './core/index.js';
import heapify from './heapify.js';
import heappop from './heappop.js';
import heappush from './heappush.js';
import heappushpop from './heappushpop.js';
import heapreplace from './heapreplace.js';
import merge from './merge.js';
import nlargest from './nlargest.js';
import nsmallest from './nsmallest.js';

export {
	core,
	heapify,
	heappop,
	heappush,
	heappushpop,
	heapreplace,
	merge,
	nlargest,
	nsmallest,
};

import {enumerate} from '@iterable-iterator/zip';
import {map} from '@iterable-iterator/map';
import {iter} from '@iterable-iterator/iter';

import {keeporder} from './core/index.js';

import heapify from './heapify.js';
import heappop from './heappop.js';
import heapreplace from './heapreplace.js';

export default function* merge(compare, ...iterables) {
	const x = [];

	for (const [order, it] of enumerate(map(iter, iterables))) {
		const rv = it.next();

		if (rv.done) continue;

		x.push([rv.value, order, it]);
	}

	const h = heapify(keeporder(compare), x);

	while (x.length > 1) {
		while (true) {
			const s = x[0];
			const value = s[0];
			const it = s[2];

			yield value;

			const rv = it.next();

			if (rv.done) {
				heappop(h);
				break;
			}

			s[0] = rv.value;

			heapreplace(h, s);
		}
	}

	if (x.length > 0) {
		const s = x[0];
		const value = s[0];
		const it = s[2];

		yield value;

		yield* it;
	}
}

import {reversed} from '@total-order/reversed';

import nsmallest from './nsmallest.js';

export default function nlargest(compare, n, iterable) {
	return nsmallest(reversed(compare), n, iterable);
}

import {min} from '@iterable-iterator/reduce';
import {sorted} from '@iterable-iterator/sorted';
import {forwardRangeIterator} from '@iterable-iterator/range';
import {iter} from '@iterable-iterator/iter';
import {_zip2} from '@iterable-iterator/zip';
import {reversed} from '@total-order/reversed';

import {keeporder} from './core/index.js';

import heapify from './heapify.js';
import heapreplace from './heapreplace.js';

export default function nsmallest(compare, n, iterable) {
	if (n === 1) {
		const sentinel = {};

		const result = min(compare, iterable, sentinel);

		return result === sentinel ? [] : [result];
	}

	if (iterable.length !== undefined && n >= iterable.length)
		return sorted(compare, iterable);

	const it = iter(iterable);

	const result = Array.from(
		_zip2(forwardRangeIterator(0, n, 1), it),
		([i, elem]) => [elem, i],
	);

	if (result.length === 0) return result;

	const h = heapify(keeporder(reversed(compare)), result);

	let top = result[0][0];

	let order = n;

	for (const elem of it) {
		if (compare(elem, top) < 0) {
			heapreplace(h, [elem, order]);

			top = result[0][0];

			++order;
		}
	}

	return sorted(
		compare,
		Array.from(result, (r) => r[0]),
	);
}

